INSERT INTO `ms_essay` VALUES (1, 1, '《无名故事》｜成为女人，需要父性的托举', '<div class=\\\"cover-con\\\" style=\\\"margin: 0px; padding: 0px; outline: 0px; zoom: 1; text-align: center;\\\"></div> <div class=\"yxl-editor\" style=\"margin: 30px 0px 0px; padding: 16px 0px 0px; outline: 0px; zoom: 1; color: #444444; font-size: 16px; line-height: 1.8; word-break: break-all;\"> <div class=\"yxl-editor-article \" style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1; min-height: 48px;\"> <p style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">最</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">近</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">在</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">影院点映了部意大利悬疑电影《无名故事》</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">，这</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">个电影名字</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">是直</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">译过</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">来</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">的，不过</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">很</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">难让人记住，而台湾翻译的是</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">《失窃神</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">画：卡拉瓦乔</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">》，电</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">影名</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">就</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">已</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">猜到了</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">故事的线</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">索</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">。</span></span></p> <p style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">&nbsp;</span></p> <p style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">这</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">是一部由真实故事改编的电影，</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">围绕卡</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">拉瓦乔的一幅名画</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">《</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">耶稣的诞生》的失窃而展开，</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">采</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">用了嵌套的方式，将真实故事</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">通过女</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">主</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">瓦</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">莱</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">里</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">亚</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">笔</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">下</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">虚构</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">的故事铺陈开来，并</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">最</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">终搬上了</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">银幕。在</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">明线与</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">暗</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">线的交织中，</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">当</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">电影落幕时，</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">故事被</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">推向了</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">高</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">潮，瓦莱里亚</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">与</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">传说中的父亲相拥，</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">既是重逢</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">，又是告别。</span></span></p> <p style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">&nbsp;</span></p> <p style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1; font-weight: 700 !important; font-size: inherit !important;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">父亲</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">之于瓦</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">莱</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">里亚是一个虚幻的符号，</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">她</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">从不知</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">父亲</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">是</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">谁</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">，也</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">不</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">知</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">他</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">去向了何处。而父亲</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">的</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">出现，让</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">瓦</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">莱里亚</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">成</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">为了一个</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">真</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">正的女性</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">。</span></span></span></p> <p style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">&nbsp;</span></p> <p style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">电</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">影公司的女秘书瓦莱里</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">亚</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">出现在镜头</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">前</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">时，是</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">一</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">位</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">梳着</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">齐耳</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">短发、戴着</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">黑框眼镜、打</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">扮</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">中性</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">，暗</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">淡无光的女性，</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">躲</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">在电脑屏</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">后</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">，</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">就</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">像一个</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">隐</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">身人。</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">她面</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">无表情，给人一种无可名</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">状</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">的压抑感。</span></span></p> <p style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">&nbsp;</span></p> <p style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">&ldquo;著</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">名编剧</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">&rdquo;</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">亚历桑德罗应付完电影公司老板的</span></span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">催</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">稿后，径直</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">坐</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">到了</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">瓦莱里亚</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">的对面，</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">瓦</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">莱里亚伸出手来，亚历桑德罗非常默契地拿出钱包，数</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">出</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">几张钞票，瓦</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">莱</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">里亚仍然盯着他，亚历桑德罗又抽出几张钞票</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">&hellip;&hellip;</span></span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">他</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">们之间似乎有着</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">&ldquo;</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">不可告人</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">&rdquo;</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">的交易。</span></span></p> <p style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">&nbsp;</span></p> <p style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">其</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">实，</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">他</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">们是一对情侣</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">。瓦</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">莱里亚是</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">编剧</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">亚历</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">桑</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">德罗背</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">后</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">的女人，</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">那</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">些让亚历桑德罗</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">出尽</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">风头的电影作品均出自她之手，作为代笔人，她贡献了自己的才华，却默默无闻</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">。</span></span></p> <p style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">&nbsp;</span></p> <p style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">&ldquo;</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">他总能想到一</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">个</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">好的开头，但很难进行下去</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">&rdquo;</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">，这正说明了亚历桑德罗在某</span></span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">种</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">层面上，可以</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">拥</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">有很多孩童式的幻想，却无法在现实层面生根。而</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">真</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">正</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">帮</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">助他实</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">现</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">这些灵感的是执笔者瓦莱里亚。</span></span></p> <p style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">&nbsp;</span></p> <p style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">这</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">个纨绔</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">男</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">子</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">背</span><span style=\"margin: 0px; padding: 0px; outline: 0px; zoom: 1;\">着瓦莱里</span><span